import React, { useState, useEffect } from 'react';

const WeatherApp = () => {
  const [activeCity, setActiveCity] = useState('Delhi');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [tempUnit, setTempUnit] = useState('C'); // Celsius by default
  const [lastUpdated, setLastUpdated] = useState(null);

  const cities = ['Delhi', 'Mumbai', 'Bangalore', 'Chennai', 'Hyderabad', 'Kolkata'];

  const getWeatherData = async (city) => {
    const apiKey = '48ed01bb564f5b6a1bebbc4c43c23e08';
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`;

    try {
        const response = await fetch(url);
        if (!response.ok) {
            throw new Error('Failed to fetch weather data');
        }
        const data = await response.json();
        setWeatherData(data);
        setError(null);
        setLastUpdated(new Date().toLocaleTimeString());
          console.log(weatherData.main.temp)
        // Get the current date and time
        const now = new Date();
        const day = now.toISOString().split('T')[0]; // Format as YYYY-MM-DD
        const hourlyTime = now.toTimeString().split(' ')[0]; // Format as HH:MM:SS

        // Check if the current time is exactly at the hour
        if (now.getMinutes() === 0) {
            // If it is exactly at the hour, send the data to the backend
            await postWeatherDataToBackend(data, city, day, hourlyTime);
        }
    } catch (err) {
        setError(err.message);
        setWeatherData(null);
    }
};


// Function to send the data to the backend
// const postWeatherDataToBackend = async (data, city) => {
//     try {
//         const backendUrl = 'http://localhost:5557/weather';
//         const response = await fetch(backendUrl, {
//             method: 'POST',
