import React, { useState, useEffect } from 'react';

const WeatherApp = () => {
  const [activeCity, setActiveCity] = useState('Delhi');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [tempUnit, setTempUnit] = useState('C'); // Celsius by default
  const [lastUpdated, setLastUpdated] = useState(null);

  const cities = ['Delhi', 'Mumbai', 'Bangalore', 'Chennai', 'Hyderabad', 'Kolkata'];

  const getWeatherData = async (city) => {
    const apiKey = '48ed01bb564f5b6a1bebbc4c43c23e08';
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`;

    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Failed to fetch weather data');
      }
      const data = await response.json();
      setWeatherData(data);
      setError(null);
      setLastUpdated(new Date().toLocaleTimeString());
    } catch (err) {
      setError(err.message);
      setWeatherData(null);
    }
  };

  useEffect(() => {
    // Initial data fetch
    getWeatherData(activeCity);

    // Set up interval to fetch data every 5 minutes
    const intervalId = setInterval(() => {
      getWeatherData(activeCity);
    }, 300000); // 300000 ms = 5 minutes

    // Cleanup interval on component unmount
    return () => clearInterval(intervalId);
  }, [activeCity]);

  const handleTempUnitChange = (unit) => {
    setTempUnit(unit);
  };

  const convertTemperature = (tempK) => {
    if (tempUnit === 'F') {
      return ((tempK - 273.15) * 9/5 + 32).toFixed(2);
    }
    return (tempK - 273.15).toFixed(2);
  };

  return (
    <section>
      <div className='container'>
        <div className='row mt-5'>
          <div className='col-lg-12 text-center fs-1 fw-bold text-dark'>
            Weather Data
          </div>
        </div>
        <div className='row'>
          <div className='col-lg-12 p-5 text-center'>
            <div className='btn-group'>
              {cities.map((city) => (
                <button
                  key={city}
                  className={`btn btn-outline-dark fw-bold ${
                    activeCity === city ? 'active' : ''
                  }`}
                  onClick={() => {
                    setActiveCity(city);
                    getWeatherData(city);
                  }}
                >
                  {city}
                </button>
              ))}
            </div>
          </div>
        </div>
        <div className='row mb-5'>
          <div className='container p-5 feedback-box'>
            <div className='row'>
              <h3 className='text-center'>
                {activeCity} Weather Data
              </h3>
            </div>

            <div className='row'>
              <div className='col-lg-12 text-center'>
                <div className='form-check form-check-inline'>
                  <input
                    className='form-check-input'
                    type='radio'
                    name='tempUnit'
                    id='celsius'
                    checked={tempUnit === 'C'}
                    onChange={() => handleTempUnitChange('C')}
                  />
                  <label className='form-check-label' htmlFor='celsius'>Celsius</label>
                </div>
                <div className='form-check form-check-inline'>
                  <input
                    className='form-check-input'
                    type='radio'
                    name='tempUnit'
                    id='fahrenheit'
                    checked={tempUnit === 'F'}
                    onChange={() => handleTempUnitChange('F')}
                  />
                  <label className='form-check-label' htmlFor='fahrenheit'>Fahrenheit</label>
                </div>
              </div>
            </div>

            <div className='row'>
              <div className='col-lg-3'></div>
              <div className='col-lg-6'>
                {error && (
                  <div className='alert alert-danger' role='alert'>
                    {error}
                  </div>
                )}

                {weatherData && (
                  <div className='text-center'>
                    <h4>{weatherData.name}</h4>
                    <p>Temperature: {convertTemperature(weatherData.main.temp)}°{tempUnit}</p>
                    <p>Weather: {weatherData.weather[0].description}</p>
                    <p>Humidity: {weatherData.main.humidity}%</p>
                    <p>Wind Speed: {weatherData.wind.speed} m/s</p>
                    {lastUpdated && (
                      <p className='text-muted'>Last Updated: {lastUpdated}</p>
                    )}
                  </div>
                )}
              </div>
              <div className='col-lg-3'></div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default WeatherApp;
